import { Component } from '@angular/core';
import { SharingServices } from '../services/sharing.services';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  Download() {
    SharingServices.downloadFile('https://localhost:44463/assets/PDFS/SamuelSsnchezCV1.pdf', 'SamuelSanchezCV');
  }
}
<header>
  <h2> Hi, I'm <strong>Samuel Sanchez Arroyo</strong></h2>
</header>
<body>
  <div class="container">
    <div class="div-texto">
      <p>
        I'm a <strong>.Net FullStack Developer</strong> <br />I am 22 years old and I am passionate about programming.
        This is my first application with angular and web development.
        If you want to know more about me, you can download my CV with the following button. You can also find my social networks in the contacts tab above. Thank you so much!<br />
        <button class="btn-download" (click)="Download()">Download CV</button>
      </p>
      <div class="div-imagen">
        <img class="imgSA" src="../assets/imgs/periquito.jpg">
      </div>
    </div>
