import { Component } from '@angular/core';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
export class CounterComponent {
  public currentCount = 0;
  public currentCount2 = 0;
  deshabilitar = false;

  public incrementCounter() {
    this.currentCount++;
  }
  public incrementCounter2() {
    this.currentCount2 += 2;
  }
  public ResetCounters() {
    this.currentCount = 0;
    this.currentCount2 = 0;
  }

}
<h1>Portfolio</h1>

<p>Con esto hacemos que se incremente de uno en uno.</p>

<p aria-live="polite">De 1 en 1: <strong>{{ currentCount }}</strong></p>
<p aria-live="polite">De 2 en 2: <strong>{{ currentCount2 }}</strong></p>

<button class="btn btn-primary" (click)="incrementCounter()">Pulsa</button>
<button class="btn btn-primary" (click)="incrementCounter2()">Pulsa</button>
<button class="btn btn-reset" (click)="ResetCounters()" [disabled]="deshabilitar">Resetear</button>
<p>Work Experience</p>
<ul>
  <li><strong></strong>SOFTWARE DEVELOPER INDRA SOFTWARE LABS</li>
</ul>
