import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
}
<div class="container">
  <div class="div-texto">
    <p>
      Hi, I'm <code>Samuel Sanchez Arroyo</code> <br /> <strong>.Net FullStack Developer</strong> <br /> If you want to know more about me, you can download my CV with the following button. You can also find my social networks in the contacts tab above
    </p>
    <button class="btn-download">Download CV</button>
  </div>
  <div class="div-imagen">
    <img class="imgSA" src="../assets/imgs/periquito.jpg">
  </div>

</div>
